// Importar useState y useEffect
import React, { useContext, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "../Components/utils/global.context";
import Swal from "sweetalert2";
import axios from "axios";

const Card = ({ id, name }) => {
  const { contextValue } = useContext(ContextGlobal);
  const { state, dispatch } = contextValue;
  const [pokemonData, setPokemonData] = useState(null);

  useEffect(() => {
    // Hacer una solicitud para obtener los datos completos del Pokémon
    axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`)
      .then(response => {
        setPokemonData(response.data);
      })
      .catch(error => {
        console.error('Error fetching Pokemon data:', error);
      });
  }, [id]);

  const addFav = () => {
    if (pokemonData) {
      dispatch({
        type: "ADD_FAV",
        payload: { id: pokemonData.id, name: pokemonData.name },
      });
      Swal.fire({
        title: "Added",
        text: `${pokemonData.name} added to favorites.`,
        icon: "success",
        showConfirmButton: false,
        timer: 2200,
      });
    }
  };

  const removeFav = () => {
    dispatch({ type: "REMOVE_FAV", payload: id });
    Swal.fire({
      title: "Removed",
      text: `${pokemonData.name} was removed to favorites.`,
      icon: "error",
      showConfirmButton: false,
      timer: 2200,
    });
  };

  const isFavorite = state.favs.some((fav) => fav.id === id);

  return (
    <div className="card">
      <Link to={`/pokemon/${id}`}>
        <div className="card-image">
          <img
            src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${id}.png`}
            alt="Pokémon"
          />
        </div>

        <div className="card-data">
          <h3>{name}</h3>
          {/* Mostrar altura y peso del Pokémon si están disponibles */}
          <p>Height: {pokemonData ? pokemonData.height : "Loading..."}</p>
          <p>Weight: {pokemonData ? pokemonData.weight : "Loading..."}</p>
        </div>
      </Link>

      {isFavorite ? (
        <button onClick={removeFav} className="favButton">
          Remove favorite
        </button>
      ) : (
        <button onClick={addFav} className="favButton">
          Add to favorites
        </button>
      )}
    </div>
  );
};

export default Card;
