import React, { useContext, useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "../Components/utils/global.context";
import Swal from "sweetalert2";

const Card = ({ id, name }) => {
  const { contextValue } = useContext(ContextGlobal);
  const { state, dispatch } = contextValue;
  const [isFavorite, setIsFavorite] = useState(state.favs.some((fav) => fav.id === id));

  const addFav = () => {
    if (!isFavorite) {
      dispatch({ type: "ADD_FAV", payload: { id, name } });
      setIsFavorite(true);
      Swal.fire({
        title: "Added",
        text: `${name} added to favorites.`,
        icon: "success",
        showConfirmButton: false,
        timer: 2200,
      });
    }
  };

  const removeFav = () => {
    dispatch({ type: "REMOVE_FAV", payload: id });
    setIsFavorite(false);
    Swal.fire({
      title: "Removed",
      text: `${name} was removed to favorites.`,
      icon: "error",
      showConfirmButton: false,
      timer: 2200,
    });
  };

  //const isFavorite = state.favs.some((fav) => fav.id === id);

  return (
    <div className="card">
      <Link to={`/pokemon/${id}`}>
        <div className="card-image">
          <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${id}.png`} alt="PokÃ©mon" />
        </div>

