          <h3>{pokemonData ? pokemonData.name : 'Loading...'}</h3>
          <p>Height: {pokemonData ? pokemonData.height : 'Loading...'}</p>
          <p>Weight: {pokemonData ? pokemonData.weight : 'Loading...'}</p>
        </div>
      </Link>

      {isFavorite ? (
        <button
          onClick={removeFav}
          className={`favButton removedFromFavs`}
        >
          Remove favorite
        </button>
      ) : (
        <button onClick={addFav} className={`favButton addedToFavs`}>
          Add to favorites
        </button>
      )}
    </div>
  );
};

export default Card;
