import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import {ContextGlobal} from '../Components/utils/global.context';





const Detail = () => {
  
  // Consumiendo el parametro dinamico de la URL deberan hacer un fetch a un user en especifico
  const { contextValue } = useContext(ContextGlobal);
  const { state } = contextValue;
  const { id } = useParams();
  const [dentist, setDentist] = useState(null);

  useEffect(() => {
    
    fetch(`https://https://pokeapi.co/api/v2/pokemon/${id}`)
      .then((response) => response.json())
      .then((data) => setDentist(data))
      .catch((error) => console.error('Error al cargar los detalles del dentista:', error));
  }, [id]);

 
  return (
    <div className={state.theme} id='detail-container'>
      <h1>Detalles odont√≥logo Id: {id} </h1>


      {dentist ? (
        <div className='detail'>     
          <table>
            <thead>
              <tr>
                <th>Nombre      </th>
  
              </tr>
            </thead>
            <tbody>
              <tr>
                <td> {dentist.name}    </td>

              </tr>
            </tbody>
          </table>
        </div>
      ) : (
        <p>Cargando...</p>
      )}

    </div>
  )
}

export default Detail
