import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import Card from '../Components/Card';
import { ContextGlobal } from '../Components/utils/global.context';

const Home = () => {
  const { contextValue } = useContext(ContextGlobal);
  const { state } = contextValue;
  const [pokemonData, setPokemonData] = useState([]);
  const [startId, setStartId] = useState(0); // Cambiado a 0 para usar offset en lugar de id
  const [renderedSeries, setRenderedSeries] = useState([]);

  useEffect(() => {
    fetchData(startId); // Llamar a fetchData con el valor inicial de startId
  }, [startId]); // Asegúrate de que useEffect se ejecute cada vez que startId cambie

  const fetchData = async (offset) => { // Modificado para aceptar offset
    try {
      const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/?offset=${offset}&limit=3`);
      setPokemonData(response.data.results);
    } catch (error) {
      console.error('Error fetching Pokémon data:', error);
    }
  };

  useEffect(() => {
    const renderPokemonSeries = async () => {
      const series = [];
      for (let i = 0; i < pokemonData.length; i++) {
        const pokemon = pokemonData[i];
        series.push(
          <div key={pokemon.url}>
            <Card key={pokemon.url} id={pokemon.url} name={pokemon.name} />
          </div>
        );
      }
      setRenderedSeries(series);
    };
    renderPokemonSeries();
  }, [pokemonData]); // Renderizar series cuando pokemonData cambie

  const handleNextPage = () => {
    setStartId(startId + 20); // Incrementar el offset en 20 para la siguiente página
  };

  const handlePreviousPage = () => {
    setStartId(Math.max(startId - 20, 0)); // Decrementar el offset en 20 para la página anterior, asegurándose de que no sea menor que 0
  };

  return (
    <main className={state.theme}>
      <h1 id="h1home">Choose your favorites</h1>
      <div className='button-container'>
        <button className="prevButton" onClick={handlePreviousPage}>Previous</button>
        <button className="nextButton" onClick={handleNextPage}>Next</button>
      </div>
      <div className='card-grid'>
        {renderedSeries}
      </div>
    </main>
  );
}

export default Home;
