import React, { useContext, useState, useEffect } from 'react';
import axios from 'axios';
import Card from '../Components/Card';
import { ContextGlobal } from '../Components/utils/global.context';
import { useHistory } from 'react-router-dom';

const getPokemonInfo = async (id) => {
  try {
    const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching Pokémon info:', error);
    return null;
  }
};

const Home = () => {
  const { contextValue } = useContext(ContextGlobal);
  const { state } = contextValue;
  const history = useHistory();
  const [pokemonData, setPokemonData] = useState([]);
  const [startId, setStartId] = useState(1);
  const [renderedSeries, setRenderedSeries] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://pokeapi.co/api/v2/pokemon/');
        setPokemonData(response.data.results);
        const storedStartId = parseInt(localStorage.getItem('startId'));
        if (!isNaN(storedStartId)) {
          setStartId(storedStartId);
        }
      } catch (error) {
        console.error('Error fetching Pokémon data:', error);
      }
    };
    fetchData();
  }, []);

  useEffect(() => {
    localStorage.setItem('startId', startId.toString());
    const renderPokemonSeries = async () => {
      const series = [];
      for (let i = startId; i < startId + 3; i++) {
        const pokemon = await getPokemonInfo(i);
        series.push(
          <div key={i}>
            {pokemon && <Card key={pokemon.id} id={pokemon.id} name={pokemon.name} />}
          </div>
        );
      }
      setRenderedSeries(series);
    };
    renderPokemonSeries();
  }, [startId, pokemonData]);

  const handleNext = () => {
    if (startId + 3 <= pokemonData.length) {
      setStartId(startId + 3);
    }
  };

  const handlePrevious = () => {
    if (startId - 3 >= 1) {
      setStartId(startId - 3);
    }
  };

  const handleDetailsClick = (id) => {
    history.push(`/details/${id}`);
  };

  return (
    <main className={state.theme}>
      <h1 id="h1home">Choose your favorites</h1>
      <div className='card-grid'>
        {renderedSeries.map(card => (
          <div key={card.props.id} onClick={() => handleDetailsClick(card.props.id)}>
            {card}
          </div>
        ))}
      </div>
      <button onClick={handlePrevious}>Previous</button>
      <button onClick={handleNext}>Next</button>
    </main>
  );
}

export default Home;
