import React, { useContext, useState, useEffect } from "react";
import { Link } from 'react-router-dom';
import { ContextGlobal } from "../Components/utils/global.context";
import axios from 'axios';
import Swal from 'sweetalert2';

const Card = ({ id }) => {
  const { contextValue } = useContext(ContextGlobal);
  const { state, dispatch } = contextValue;
  const [pokemonData, setPokemonData] = useState(null);

  useEffect(() => {
    // Hacer una solicitud HTTP para obtener información sobre el Pokémon
    axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`)
      .then(response => {
        setPokemonData(response.data);
      })
      .catch(error => {
        console.error('Error fetching Pokemon data:', error);
        // Manejar el error si la solicitud falla
      });
  }, [id]);

  const addFav = () => {
    if (pokemonData) {
      // Aquí iría la lógica para agregar la tarjeta en el almacenamiento local
      dispatch({ type: "ADD_FAV", payload: { id: pokemonData.id, name: pokemonData.name, height: pokemonData.height, weight: pokemonData.weight } });
      Swal.fire({
        title: 'Agregado',
        text: `${pokemonData.name} agregado a Pokémon favoritos.`,
        icon: 'success',
        showConfirmButton: false,
        timer: 2200
      })
    }
  };

  const removeFav = () => {
    dispatch({ type: "REMOVE_FAV", payload: id });
    Swal.fire({
      title: 'Eliminado',
      text: `Pokémon ${pokemonData.name} eliminado de favoritos.`,
      icon: 'error',
      showConfirmButton: false,
      timer: 2200
    })
  };

  return (
    <div className="card">
      <Link to={`/pokemon/${id}`}>
        <div className="card-image">
          <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${id}.png`} alt="Pokémon" />
        </div>

        <div className="card-data">
          <h5>{pokemonData && pokemonData.name}</h5>
          <p>Height: {pokemonData && pokemonData.height}</p>
          <p>Weight: {pokemonData && pokemonData.weight}</p>
        </div>
      </Link>

      {state.favs.some((fav) => fav.id === id) ? (
        <button onClick={removeFav} className="favButton">
          Eliminar favorito
        </button>
      ) : (
        <button onClick={addFav} className="favButton">
          Agregar a favorito
        </button>
      )}
    </div>
  );
};

export default Card;
