import Card from '../Components/Card';
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const getPokemonInfo = async (id) => {
  try {
    const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching Pokémon info:', error);
    return null;
  }
};

const Home = () => {
  const [pokemonIds, setPokemonIds] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  const [startId, setStartId] = useState(1);

  const fetchPokemonIds = async () => {
    setLoading(true);
    try {
      const response = await axios.get('https://pokeapi.co/api/v2/pokemon/');
      const { results } = response.data;
      const ids = results.map(pokemon => {
        const urlParts = pokemon.url.split('/');
        return parseInt(urlParts[urlParts.length - 2]);
      });
      setPokemonIds(ids);
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchPokemonIds();
  }, []);

const getPokemonInfo = async (id) => {
  try {
    const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
    return response.data;
  } catch (error) {
    console.error('Error fetching Pokémon info:', error);
    return null;
  }
};

const renderPokemonSeries = () => {
  const series = [];
  for (let i = startId; i < startId + 3; i++) {
    const pokemon = getPokemonInfo(i);
    // Resto del código para renderizar las tarjetas de Pokémon...
  }
  return series;
};



  const handleNext = () => {
    if (startId + 3 <= pokemonIds[pokemonIds.length - 1]) {
      setStartId(startId + 3);
    }
  };

  const handlePrevious = () => {
    if (startId - 3 >= 1) {
      setStartId(startId - 3);
    }
  };

  return (
    <div>
      {loading && <p>Cargando...</p>}
      {error && <p>Error: {error.message}</p>}
      {!loading && !error && (
        <>
          <h2>Pokémones</h2>
          <div>{renderPokemonSeries()}</div>
          <button onClick={handlePrevious}>Anterior</button>
          <button onClick={handleNext}>Siguiente</button>
        </>
      )}
    </div>
  );
};

export default Home;
