import React, { useState, useEffect, useContext } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import { ContextGlobal } from '../Components/utils/global.context';

const Detail = () => {
  const { contextValue } = useContext(ContextGlobal);
  const { state } = contextValue;
  const { id } = useParams();
  const [pokemon, setPokemon] = useState(null);

  useEffect(() => {
    axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`)
      .then((response) => setPokemon(response.data)) // Utiliza response.data en lugar de response.json()
      .catch((error) => console.error('Error loading pokemon:', error));
  }, [id]);

  return (
    <div className={state.theme} id='detail-container'>
      <h1>Pokemon details Id: {id} </h1>

      {pokemon ? (
        <div className='detail'>
          <table>
            <thead>
              <tr>
                <th>Name</th>
                <th>Height</th>
                <th>Weight</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td>{pokemon.name}</td>
                <td>{pokemon.height}</td>
                <td>{pokemon.weight}</td>
              </tr>
            </tbody>
          </table>
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default Detail;
