import React, { useContext, useEffect, useState } from "react";
import Card from "../Components/Card";
import { ContextGlobal } from "../Components/utils/global.context";
import axios from 'axios';
import Swal from 'sweetalert2';

const Favs = () => {
  const { contextValue } = useContext(ContextGlobal);
  const { state, dispatch } = contextValue;
  const [pokemonData, setPokemonData] = useState([]);

  useEffect(() => {
    // Fetch data for each favorite Pokemon
    const fetchDataForFavorites = async () => {
      const favData = await Promise.all(
        state.favs.map(async (id) => {
          try {
            const response = await axios.get(`https://pokeapi.co/api/v2/pokemon/${id}`);
            return response.data;
          } catch (error) {
            console.error('Error fetching Pokemon data:', error);
            return null;
          }
        })
      );
      setPokemonData(favData.filter(pokemon => pokemon !== null)); // Filter out null values
    };

    fetchDataForFavorites();
  }, [state.favs]);

  const clearAllFavs = () => {
    dispatch({ type: "CLEAR_FAVS" });

    Swal.fire({
      title: 'Favorites were deleted',
      text: 'You no longer have favorites.',
      icon: 'error',
      showConfirmButton: false,
      timer: 2200
    });
  };

  return (
    <div className={state.theme} id="fav-container">
      <h1>Favorites</h1>
      <div className="card-grid">
        {pokemonData.map((pokemon) => (
          <Card
            key={pokemon.id}
            name={pokemon.name}
            height={pokemon.height}
            weight={pokemon.weight}
            id={pokemon.id}
          />
        ))}
      </div>
      <button className="favButton" onClick={clearAllFavs}>Clear all</button>
    </div>
  );
};

export default Favs;
