import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
import { ContextGlobal } from "../Components/utils/global.context";
import Swal from "sweetalert2";

const Card = ({ id, name, height, weight, isFavorite }) => {
  const { contextValue } = useContext(ContextGlobal);
  const { state, dispatch } = contextValue;

  const addFav = () => {
    dispatch({ type: "ADD_FAV", payload: { id, name, height, weight } });
    Swal.fire({
      title: "Added",
      text: `${name} added to favorites.`,
      icon: "success",
      showConfirmButton: false,
      timer: 2200,
    });
  };

  const removeFav = () => {
    dispatch({ type: "REMOVE_FAV", payload: id });
    Swal.fire({
      title: "Removed",
      text: `${name} was removed to favorites.`,
      icon: "error",
      showConfirmButton: false,
      timer: 2200,
    });
  };

  return (
    <div className="card">
      <Link to={`/pokemon/${id}`}>
        <div className="card-image">
          <img src={`https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/official-artwork/${id}.png`} alt="PokÃ©mon" />
        </div>

        <div className="card-data">
          <h3>{name ? name : 'Loading...'}</h3>
          <p>Height: {height ? height : 'Loading...'}</p>
          <p>Weight: {weight ? weight : 'Loading...'}</p>
        </div>
      </Link>

      {isFavorite ? (
        <button onClick={removeFav} className={`favButton favorite`}>
          Remove favorite
        </button>
      ) : (
        <button onClick={addFav} className={`favButton`}>
          Add to favorites
        </button>
      )}
    </div>
  );
};

export default Card;
